package com.testek.projects.client.rest;import com.testek.clients.api.APIBase;import com.testek.consts.FrameConst;import io.restassured.response.Response;import lombok.Getter;import lombok.extern.slf4j.Slf4j;import org.json.JSONObject;@Getter@Slf4jpublic class LoginServices extends APIBase {    @Getter    private static final LoginServices instance = new LoginServices();    private LoginServices() {    }    public Response callLoginAPI(String username, String password) {        return callRestAPI(FrameConst.HTTPMethod.POST,                createDefaultRequest(APIPath.LOGIN_API, true)                        .header("Content-Type", "application/json")                        .body(new JSONObject()                                .put("username", username)                                .put("password", password)                                .toString())        );    }    public String getLoginToken(String username, String password) {        Response response = callLoginAPI(username, password);        log.info("Token: {}", response.jsonPath().getString("access_token"));        return response.jsonPath().getString("access_token");    }}